import React from "react";
import { Link, useNavigate } from "react-router-dom";
/* import "./styles/Login.css"; */

const Login = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = React.useState({
    username: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // maneja el registro aquí

    // Ejemplo de redirección después del manejo
    navigate("/dashboard"); // Reemplaza "/dashboard" con la ruta deseada
  };

  return (
    <div className="login">
      <p className="login__welcome">¡Bienvenido de nuevo!</p>
      <form onSubmit={handleSubmit} className="login__form">
        <label htmlFor="username">Nombre de usuario:</label>
        <input
          required
          id="username"
          name="username"
          type="text"
          value={formData.username}
          onChange={handleChange}
        />
        <label htmlFor="password">Contraseña:</label>
        <input
          required
          id="password"
          name="password"
          type="password"
          value={formData.password}
          onChange={handleChange}
        />
        <div className="login__button-container">
          <button type="submit" className="login__link">
            Log in
          </button>
        </div>
      </form>
      <div className="login__signup">
        <p>¿Listo para comenzar el viaje?</p>
        <Link to="/register" className="signup__link">
          Iniciar sesión
        </Link>
      </div>
    </div>
  );
};

export default Login;
