export const BASE_URL = "https://register.nomoreparties.co";

export const register = async (contraseña, email) => {
  try {
    const response = await fetch(`${BASE_URL}/signup`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        contraseña: contraseña,
        email: email,
      }),
    });

    if (!response.ok) {
      if (response.status === 400) {
        // Uno de los campos se rellenó de forma incorrecta
        throw new Error(
          "Error: Uno de los campos se rellenó de forma incorrecta"
        );
      } else {
        throw new Error(`Error: ${response.status} - ${response.statusText}`);
      }
    }

    const responseBody = await response.json();
    console.log(responseBody); // Muestra la respuesta del servidor

    return responseBody.data; // Devuelve solo la parte de datos de la respuesta
  } catch (error) {
    console.error("Error:", error.message);
    return { error: true, message: error.message };
  }
};
